import Foundation
import SwiftUI

struct EuropeanRoute: Identifiable, Codable {
    let id = UUID()
    let name: String
    let country: String
    let distanceKm: Double
    let description: String
    let highlights: [String]
    let difficulty: Difficulty
    let bestMonths: String
    let startPoint: String
    let endPoint: String
    var isFavorite: Bool = false
    
    enum Difficulty: String, Codable {
        case easy = "Easy"
        case moderate = "Moderate"
        case challenging = "Challenging"
        case expert = "Expert"
    }
    
    var fuelCost: Double {
        UserProfileManager.shared.calculateFuelCost(distanceKm: distanceKm)
    }
    
    var fuelCostFormatted: String {
        guard let motorcycle = UserProfileManager.shared.profile.selectedMotorcycle else {
            return "Select motorcycle in profile"
        }
        return String(format: "%.0f kr", fuelCost)
    }
}

