    var countries: [String] {
        Array(Set(routes.map { $0.country })).sorted()
    }
    
    func routes(for country: String) -> [EuropeanRoute] {
        routes.filter { $0.country == country }.sorted { $0.name < $1.name }
    }
    
    func search(query: String) -> [EuropeanRoute] {
        if query.isEmpty {
            return routes
        }
        return routes.filter {
            $0.name.localizedCaseInsensitiveContains(query) ||
            $0.country.localizedCaseInsensitiveContains(query) ||
            $0.description.localizedCaseInsensitiveContains(query)
        }
    }
