// MARK: - Difficulty Extensions
extension EuropeanRoute.Difficulty {
    var icon: String {
        switch self {
        case .easy: return "leaf.fill"
        case .moderate: return "bicycle"
        case .challenging: return "mountain.2.fill"
        case .expert: return "bolt.fill"
        }
    }
    
    var color: Color {
        switch self {
        case .easy: return .green
        case .moderate: return .blue
        case .challenging: return .orange
        case .expert: return .red
        }
    }
}
